$(document).ready((function(){$.get("https://raw.githubusercontent.com/kgbzen/archive/main/blog.json",(function(t){var e=JSON.parse(t).blog,o=$("#items-list");for(let t=0;t<e.length;t++){var s=e[t],i=new Date(1e3*s.date);o.append(`<li class="list-group-item mb-1 impress-group-item" url="${s.url}" onclick="loadMarkDown(this)"><div class="row"><div class="col-auto d-flex justify-content-center my-auto"><img class="img-fluid shadow" src="https://kgbapi.herokuapp.com/square/B12345?t=${t}" width="85px" style="border: 1px solid rgb(0,0,0);" /></div><div class="col"><h5 style="font-family: 'Noto Sans JP', sans-serif;font-weight: bold;">${s.name}</h5><code>${new Intl.DateTimeFormat("en-GB",{weekday:"short",year:"numeric",month:"short",day:"numeric"}).format(i)}</code><p style="font-family: 'Noto Sans JP', sans-serif;">${s.description}</p></div></div></li>`)}}))}));